    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        StringTokenizer lines = new StringTokenizer(jTextArea1.getText(), "\n");
        while (lines.hasMoreTokens()) {
            String line = lines.nextToken();

            jPanel2.add(new JLabel(line));
            StringTokenizer tok = new StringTokenizer(line);
            try {
                Date tempDate = timeStamp.parse(tok.nextToken());
                if (d != null) {
                    String s = "" + (tempDate.getTime() - d.getTime());
                    if(s.equals("0")) {
                        s = "";
                    }
                    JLabel timeLabel = new JLabel(s);
                    jPanel3.add(timeLabel);
                }
                d = tempDate;

            } catch (Exception ex) {
                ex.printStackTrace();
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

