    private static List<String> findEmailAddresses(String response) {
        List<String> emails = new ArrayList<>();
        String searchString = response;
        String regex = "<a href=\"mailto:[&#0123456789]+\">";
        Pattern pattern = Pattern.compile(regex);
        Matcher matcher = pattern.matcher(searchString);
        while (matcher.find()) {
            int start = matcher.start();
            int end = matcher.end();
            if (start == end) {
                break;
            }
            int quoteStart = searchString.indexOf("\"", matcher.start()) + 8;
            int quoteEnd = searchString.indexOf("\"", quoteStart + 1);
            emails.add(decodeEmailAddress(searchString.substring(quoteStart, quoteEnd)));
            searchString = searchString.substring(quoteEnd);
            matcher = pattern.matcher(searchString);
        }

        return emails;
    }

    private static String decodeEmailAddresses(String response) {
        StringBuilder sb = new StringBuilder();
        String searchString = response;
        String regex = "<a href=\"mailto:[&#0123456789]+\">";
        Pattern pattern = Pattern.compile(regex);
        Matcher matcher = pattern.matcher(searchString);
        String endRegex = "</a>";
        Pattern endPattern = Pattern.compile(endRegex);
        Matcher endMatcher = endPattern.matcher(searchString);
        while (matcher.find()) {
            endMatcher.find();
            int start = matcher.start();
            int end = matcher.end();
            if (start == end) {
                break;
            }
            int quoteStart = searchString.indexOf("\"", matcher.start()) + 8;
            int quoteEnd = searchString.indexOf("\"", quoteStart + 1);
            sb.append(searchString.substring(0, start));
            sb.append(decodeEmailAddress(searchString.substring(quoteStart, quoteEnd)));
            end = endMatcher.end();
            searchString = searchString.substring(end);
            matcher = pattern.matcher(searchString);
            endMatcher = endPattern.matcher(searchString);
        }

        return sb.toString();
    }

    private static String decodeEmailAddress(String encodedEmail) {
        String retVal = "";
        StringTokenizer tok = new StringTokenizer(encodedEmail, "&");
        while (tok.hasMoreTokens()) {
            String s = tok.nextToken().substring(1);
            int i = new Integer(s);

            retVal += ((char) i);
        }
        return retVal;
    }

